import React, { Component } from 'react';
class Login extends Component {
    constructor(){
        super();
        this.state = {
         username: '',
         password: '',
        };
        this.login = this.login.bind(this);
        this.onChange = this.onChange.bind(this);

    }
    login() {


    }

    onChange(e){
        this.setState({[e.target.name]:e.target.value});
    }

    render() { 
        return (
            <div  className="row" id="Body">
                <center>
                    <div className="medium-5 columns left">
                        <h4>Login Page</h4>
                        <label>Username</label><br></br>
                        <input type="text" name="username" placeholder="Username" onChange={this.onChange}/><br>
                        </br>
                        <label>Password</label><br></br>
                        <input type="password" name="password"  placeholder="Password" onChange={this.onChange}/><br>
                        </br>
                        <input type="submit" className="btn btn-primary" value="Login" onClick={this.login}/>
                        <a href="/signup">Registration</a>
                    </div>
                </center>

            </div>   
        );
    }
}

export default Login;
