import React, { Component } from 'react';
import styles from "./custom.module.css" ;
import { Link } from 'react-router-dom';
class Login extends Component {
    constructor(){
        super();
        this.state = {
         username: '',
         password: '',
        };

        this.onChange = this.onChange.bind(this);

    }
    onChange(e){
        this.setState({[e.target.name]:e.target.value});
    }

    render() { 
        return (

            <div>
                <div className="container-fluid">
                    <div className="jumbotron" id={styles.side}>
                        <h1 className="text-center font-weight-light">CONTACTS</h1>
                        <Link className={styles.buy}to='/register'><button  className="btn btn-primary" >SignUp</button></Link>
                    </div>
                </div>
                <div className="container">
                    <form  method="post" action="login.php">
                        <div className="form-group">
                            <div className="row">
                                <div className="col-xs-3">
                                User Name:
                                <input className="form-control" id="username" name="username" type="text" placeholder="username"/>
                                </div>
                            </div>
                            <br></br>
                            <div className="row">
                                <div className="col-xs-3">
                                Password:
                                <input className="form-control" id="password" name="password" type="password" placeholder="password"/>
                                </div>
                            </div>
                            <br></br>
                            <div className="row">
                                <div className="col-xs-3">
                                    <input type="submit" className="btn btn-primary" value="login"/>
                                </div>
                            </div>
                        </div>

                    </form>
                </div>

                <footer class="page-footer font-small special-color-dark pt-4">
                    <div class="footer-copyright text-center py-3">© 2020 Copyright:
                        <a href="vinayreddykallu@gmail.com"> EMAIL</a>
                    </div>
                </footer>

            </div>  

        );
    }
}

export default Login;
