import React, { Component } from 'react';
import styles from "./custom.module.css" ;
import GoogleContacts from 'react-google-contacts';
import axios from 'axios';
class Contacts extends Component {
    state = {
        isclicked : false,
        issearch :false,
        username: '',
        contacts: [],
    }

    handleSubmit = e => {
        this.setState({isclicked: true});
        e.preventDefault();
        console.log(this.state.username.length);
            const url = "http://localhost:8080/reg";
            axios.get(url,{
            }).then(response => response.data)
            .then((data) => {
                this.setState({ contacts: data })
                console.log(this.state.contacts)
                })

    }


    render() {  

            return ( 
                <div>
                    <div className="container-fluid">
                        <div className="jumbotron" id={styles.side}>
                            <h1 className="text-center font-weight-light">CONTACTS</h1>
                            <button id={styles.buy1} onClick={this.handleSubmit}  className="btn btn-outline-secondary">View Contacts</button>
                            <br></br><marquee behavior="scroll" direction="left"><h2>Here are your contacts!</h2></marquee>

                        </div>
                    </div>
                    <div className="container-fluid">
                        <table border='1' width='100%' >
                        <thead>
                                <tr>
                                    <th>UserName</th>
                                    <th>Email</th>
                                    <th>Phone</th>
                                </tr>
                        </thead>
                            <tbody>
                                {this.state.contacts.map((contact) => (

                                <tr>
                                    <td>{ contact.username }</td>
                                    <td >{ contact.email }</td>
                                    <td >{ contact.phone }</td>

                                </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    </div>

            );

    }
}

export default Contacts;
