import React, { Component } from 'react';
import styles from "./custom.module.css" ;
import axios from 'axios';
import { Link } from 'react-router-dom';
class Login extends Component {
    state = {
        username: '',
        password: '',

    }
    handleSubmit = e => {
        e.preventDefault();
        console.log(this.state.username);
        console.log(this.state.password);
        if(this.state.username.length==0)
            alert("Username should not be Empty!");
        if(this.state.password.length==0)
            alert("password should not be Empty!");

        let formData = new FormData();
        formData.append("username",this.state.username);
        formData.append("password",this.state.password);
        const url="http://localhost:8080/login";
        axios.post(url,formData)
        .then(res=> 
            window.location= "/contacts"
        )
        .catch(err=>  alert("invalid credentials")
        );


    }


    render() { 
        return (

            <div>
                <div className="container-fluid">
                    <div className="jumbotron" id={styles.side}>
                        <h1 className="text-center font-weight-light">CONTACTS</h1>
                        <Link className={styles.buy}to='/register'><button  className="btn btn-primary" >SignUp</button></Link>
                    </div>
                </div>
                <div className="container">
                    <form >
                        <div className="form-group">
                            <div className="row">
                                <div className="col-xs-3">
                                User Name:
                                <input className="form-control" id="username" name="username" type="text" placeholder="username"
                                onChange={e => this.setState({ username: e.target.value })}/>
                                </div>
                            </div>
                            <br></br>
                            <div className="row">
                                <div className="col-xs-3">
                                Password:
                                <input className="form-control" id="password" name="password" type="password" placeholder="password"
                                onChange={e => this.setState({ password: e.target.value })}/>
                                </div>
                            </div>
                            <br></br>
                            <div className="row">
                                <div className="col-xs-3">
                                    <input type="submit" onClick={this.handleSubmit} className="btn btn-primary" value="login"/>
                                </div>
                            </div>
                        </div>

                    </form>
                </div>

                <footer className="container py-5">
                    <div className="row">
                        <div className="col-6 col-md">
                            <p><strong>About us</strong></p>
                             <p className="text-muted">This  application  displays you the contact details of all the 
                                 users who are using this application.Thanks for using our application.
                                 Hope you have liked our application.Please feel free to contact us for any further help!</p>   


                        </div>
                        <div className="col-6 col-md">
                            <address>
                                <ul className="list-unstyled text-small">
                                    <li><strong>Corporate Office</strong></li>
                                    <li className="text-muted">17/1/382/B/97</li>
                                    <li className="text-muted">Champapet, Hyderabad - 500079</li>
                                    <li className="text-muted">Telangana, India</li>
                                    <li className="text-muted">Phone: +91 800 83 17 399</li>
                                </ul>
                            </address>

                        </div>

                    </div>
                    <div className="footer-copyright text-center py-3">Â© 2020 Copyright:
                        <a href="mailto:vinayreddykallu@gmail.com"> EMAIL US</a>
                    </div>
                </footer>



            </div>  

        );
    }
}

export default Login;
